import React, { useState } from 'react';
import TimeField from 'react-simple-timefield';

const CreateClass = () => {
    const [ state, setState ] = useState({
        className:'',
        classType:'',
        startTime:'',
        duration: '',
        level: '',
        location: '',
        currAttendees: 0,
        maxAttendees:0
    })

    const handleChange = e => {
        console.log(state)
        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }
    const handleSubmit = e => {
        e.preventDefault();
    }
    return (
        <div className="create-class form">
            <form onSubmit={handleSubmit}>
                <h1>Create Class</h1>
                <label>
                    Class Name
                    <input
                    name="className"
                    type="text"
                    value={state.className}
                    onChange={handleChange}
                    />
                </label>
                <label>
                    Class Type
                    <select name="classType" value={state.classType} onChange={handleChange}>
                        <option value="none" selected>Class type</option>
                        <option value="flex">Flexibility exercise</option>
                        <option value="aero">Aerobic exercise</option>
                        <option value="anaero">Anaerobic exercise</option>
                        <option value="other">other</option>
                    </select>
                </label>
                <label>
                    Start Time

                </label>

            </form>
        </div>
    )
}
export default CreateClass;
