import React, { useState } from 'react';
import styled from 'styled-components';
import TimeField from 'react-simple-timefield';

const CreateClass = () => {
    const [ state, setState ] = useState({
        className:'',
        classType:'',
        startTime:'',
        duration: '',
        level: '',
        location: '',
        currAttendees: 0,
        maxAttendees:0
    })

    const handleChange = e => {
        console.log(state)
        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }
    const handleSubmit = e => {
        e.preventDefault();

    }
    return (<ComponentContainer>
    <ModalContainer>

        <div className="create-class form">
            <FormGroup onSubmit={handleSubmit}>
                <h1>Create Class</h1>
                <Label>
                    Class Name
                    <Input
                    name="className"
                    type="text"
                    value={state.className}
                    onChange={handleChange}
                    />
                </Label>
                <Label>
                    Class Type
                    <Select name="classType" value={state.classType} onChange={handleChange}>
                        <option value="none" selected>Class type</option>
                        <option value="flex">Flexibility exercise</option>
                        <option value="aero">Aerobic exercise</option>
                        <option value="anaero">Anaerobic exercise</option>
                        <option value="other">other</option>
                    </Select>
                </Label>
                <Label>
                    Start Time
                    <Select name="level" value={state.startTime} onChange={handleChange}>                        
                        <option value="am">AM</option>
                        <option value="pm">PM</option>                        
                    </Select>
                    <TimeField
                    name="startTime"
                    value={state.startTime}
                    onChange={handleChange}
                    colon=":"

                    />
                </Label>
                <Label>
                    Duration
                    <Input
                    name="duration"
                    type="number"
                    value={state.duration}
                    onChange={handleChange}
                    />
                </Label>
                <Label>
                    Level
                    <Select name="level" value={state.level} onChange={handleChange}>
                        <option value="none" selected>Select Level</option>
                        <option value="beginner">Beginner</option>
                        <option value="intermediate">Intermediate</option>
                        <option value="advanced">Advanced</option>
                        <option value="other">other</option>
                    </Select>
                </Label>
                <Label>
                    Location
                    <Select name="location" value={state.location} onChange={handleChange}>
                        <option value="none" selected>Select Location</option>
                        <option value="upperEast">Upper East</option>
                        <option value="upperWest">Upper West</option>
                        <option value="midtown">Midtown</option>
                        <option value="downtown">Downtown</option>
                        <option value="other">other</option>
                    </Select>
                </Label>
                <Label>
                    Current attendees
                    <Input
                    name="attendees"
                    type="number"
                    value={state.attendees}
                    onChange={handleChange}
                    />
                </Label>
                <Label>
                    Max Attendees
                    <Input
                    name="maxAttendees"
                    type="number"
                    value={state.maxAttendees}
                    onChange={handleChange}
                    />
                </Label>
                <Button>Submit</Button>
            </FormGroup>
        </div>
    </ModalContainer>    
</ComponentContainer>)
}
export default CreateClass;

const ComponentContainer = styled.div`
    height: 70%;
    justify-content: center;
    align-items: center;
    display:flex;
`

const ModalContainer = styled.div`
    width: 500px;
    background: white;
    padding: 2rem;
    text-align: center;
`

const Label = styled.label`
    display: block;
    text-align: left;
    font-size: 1.5rem;
`

const FormGroup = styled.form`
    padding:1rem;
`

const Input = styled.input`
    font-size: 1rem;
    padding: 1rem 0;
    width:100%;
`
const Select = styled.select`
    font-size: 1rem;
    padding: 1rem 0;
    width:100%;
`

const Button = styled.button`
    padding:1rem;
    width: 100%;
`
